      Ori: OPTIONAL Rotation // undefiniert = 100.0 //;
      HAli: OPTIONAL HALIGNMENT // undefiniert = Center //;
      VAli: OPTIONAL VALIGNMENT // undefiniert = Half //;
      Groesse: OPTIONAL Schriftgroesse // undefiniert = mittel //;
